import { Component } from '@angular/core';
import { Sort } from '@angular/material/sort';
import { MatTableDataSource } from '@angular/material/table';
import { Router } from '@angular/router';
import {
  apiResultFormat,
  DataService,
  pageSelection,
  routes,
} from 'src/app/core/core.index';
import { customerList } from 'src/app/shared/model/page.model';
import { PaginationService, tablePageSize } from 'src/app/shared/shared.index';
import { SweetalertService } from 'src/app/shared/sweetalert/sweetalert.service';
import {CustomerModel} from "src/api-service/model/CustomerModel";
import {CustomerService} from "src/api-service/service/CustomerService";


@Component({
  selector: 'app-customerlist',
  templateUrl: './customerlist.component.html',
  styleUrls: ['./customerlist.component.scss'],
})
export class CustomerlistComponent {
  public routes = routes;
  initChecked = false;
  // public tableData: Array<customerList> = [];
  public tableData: Array<CustomerModel> = [];
  // pagination variables
  public pageSize = 10;
  public serialNumberArray: Array<number> = [];
  public totalData = 0;
  showFilter = false;
  dataSource!: MatTableDataSource<customerList>;
  public searchDataValue = '';
  //** / pagination variables
  constructor(
    private data: DataService,
    private pagination: PaginationService,
    private sweetalert: SweetalertService,
    private router: Router,
    private customerService :CustomerService
  ) {
    this.pagination.tablePageSize.subscribe((res: tablePageSize) => {
      if (this.router.url == this.routes.customerList) {
        this.getTableData({ skip: res.skip, limit: res.limit });
        this.pageSize = res.pageSize;
      }
    });
  }

  private getTableData(pageOption: pageSelection): void {
    this.customerService.getCustomerList().subscribe((apiRes: apiResultFormat) => {
      this.tableData = [];
      this.serialNumberArray = [];
      this.totalData = apiRes.totalData;
      apiRes.data.map((res: customerList, index: number) => {
        const serialNumber = index + 1;
        if (index >= pageOption.skip && serialNumber <= pageOption.limit) {
          res.sNo = serialNumber;
          this.tableData.push(res);
          this.serialNumberArray.push(serialNumber);
        }
      });
      this.dataSource = new MatTableDataSource<customerList>(this.tableData);
      this.pagination.calculatePageSize.next({
        totalData: this.totalData,
        pageSize: this.pageSize,
        tableData: this.tableData,
        serialNumberArray: this.serialNumberArray,
      });
    });
  }
  deleteBtn() {
    this.sweetalert.deleteBtn();
  }


  public searchData(value: string): void {
    this.dataSource.filter = value.trim().toLowerCase();
    this.tableData = this.dataSource.filteredData;
  }

  public sortData(sort: Sort) {
    const data = this.tableData.slice();

    if (!sort.active || sort.direction === '') {
      this.tableData = data;
    } else {
      // eslint-disable-next-line @typescript-eslint/no-explicit-any
      this.tableData = data.sort((a: any, b: any) => {
        // eslint-disable-next-line @typescript-eslint/no-explicit-any
        const aValue = (a as any)[sort.active];
        // eslint-disable-next-line @typescript-eslint/no-explicit-any
        const bValue = (b as any)[sort.active];
        return (aValue < bValue ? -1 : 1) * (sort.direction === 'asc' ? 1 : -1);
      });
    }
  }

  selectAll(initChecked: boolean) {
    if (!initChecked) {
      this.tableData.forEach((f) => {
        f.isSelected = true;
      });
    } else {
      this.tableData.forEach((f) => {
        f.isSelected = false;
      });
    }
  }
}
<div class="page-header">
  <div class="page-title">
    <h4>Customer List</h4>
    <h6>Manage your Customers</h6>
  </div>
  <div class="page-btn">
    <a [routerLink]="routes.addCustomer" class="btn btn-added"
      ><img src="assets/img/icons/plus.svg" alt="img" />Add Customer</a
    >
  </div>
</div>
<!-- /product list -->
<div class="card">
  <div class="card-body">
    <div class="table-top">
      <div class="search-set">
        <div class="search-path">
          <a
            (click)="showFilter = !showFilter"
            [class.setclose]="showFilter === true"
            class="btn btn-filter"
            id="filter_search"
          >
            <img
              *ngIf="showFilter === false"
              src="assets/img/icons/filter.svg"
              alt="img"
            />
            <span
              ><img
                *ngIf="showFilter === true"
                src="assets/img/icons/closes.svg"
                alt="img"
            /></span>
          </a>
        </div>
        <div class="search-input">
          <a class="btn btn-searchset"
            ><img src="assets/img/icons/search-white.svg" alt="img"
          /></a>
          <div class="dataTables_filter">
            <label>
              <input
                [(ngModel)]="searchDataValue"
                (ngModelChange)="searchData(searchDataValue)"
                type="search"
                class="form-control"
                placeholder="Search..."
              />
            </label>
          </div>
        </div>
      </div>
      <div class="wordset">
        <ul>
          <li>
            <a data-bs-toggle="tooltip" data-bs-placement="top" title="pdf"
              ><img src="assets/img/icons/pdf.svg" alt="img"
            /></a>
          </li>
          <li>
            <a data-bs-toggle="tooltip" data-bs-placement="top" title="excel"
              ><img src="assets/img/icons/excel.svg" alt="img"
            /></a>
          </li>
          <li>
            <a data-bs-toggle="tooltip" data-bs-placement="top" title="print"
              ><img src="assets/img/icons/printer.svg" alt="img"
            /></a>
          </li>
        </ul>
      </div>
    </div>
    <!-- /Filter -->
    <div
      class="card mb-0 border-0"
      [ngClass]="{
        'show-sub-menu': showFilter === true,
        'hide-sub-menu': showFilter === false
      }"
    >
      <div class="card-body pb-0">
        <div class="row">
          <div class="col-lg-2 col-sm-6 col-12">
            <div class="form-group">
              <input type="text" placeholder="Enter Customer Code" />
            </div>
          </div>
          <div class="col-lg-2 col-sm-6 col-12">
            <div class="form-group">
              <input type="text" placeholder="Enter Customer Name" />
            </div>
          </div>
          <div class="col-lg-2 col-sm-6 col-12">
            <div class="form-group">
              <input type="text" placeholder="Enter Phone Number" />
            </div>
          </div>
          <div class="col-lg-2 col-sm-6 col-12">
            <div class="form-group">
              <input type="text" placeholder="Enter Email" />
            </div>
          </div>
          <div class="col-lg-1 col-sm-6 col-12 ms-auto">
            <div class="form-group">
              <a class="btn btn-filters ms-auto"
                ><img src="assets/img/icons/search-whites.svg" alt="img"
              /></a>
            </div>
          </div>
        </div>
      </div>
    </div>
    <!-- /Filter -->
    <div class="table-responsive">
            <table
        (matSortChange)="sortData($event)"
        matSort
        matSortActive="Sno"
        matSortDirection="asc"
        matSortDisableClear class="table datanew">
        <thead>
          <tr>
            <th>
              <label class="checkboxs" (click)="selectAll(initChecked)">
                <input type="checkbox" [(ngModel)]="initChecked" />
                <span class="checkmarks"></span>
              </label>
            </th>
            <th mat-sort-header="CustomerName">Customer Name</th>
            <th mat-sort-header="code">Code</th>
            <th mat-sort-header="Customer">Customer</th>
            <th mat-sort-header="Phone">Phone</th>
            <th mat-sort-header="email">Email</th>
            <th mat-sort-header="Country">Country</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let people of tableData; let i = index">
            <td>
              <label class="checkboxs">
                <input type="checkbox" [(ngModel)]="people.isSelected" />
                <span class="checkmarks"></span>
              </label>
            </td>
            <td class="productimgname">
              <a href="javascript:void(0);" class="product-img">
                <img [src]="people.img" alt="product" />
              </a>
              <a href="javascript:void(0);">{{ people.CustomerName }}</a>
            </td>
            <td>{{ people.code }}</td>
            <td>{{ people.Customer }}</td>
            <td>{{ people.Phone }}</td>
            <td>{{ people.email }}</td>
            <td>{{ people.Country }}</td>
            <td>
              <a class="me-3" [routerLink]="routes.editCustomer">
                <img src="assets/img/icons/edit.svg" alt="img" />
              </a>
              <a
                (click)="deleteBtn()"
                class="me-3 confirm-text"
                href="javascript:void(0);"
              >
                <img src="assets/img/icons/delete.svg" alt="img" />
              </a>
            </td>
          </tr>
          <tr *ngIf="tableData.length === 0">
            <td colspan="12">
              <h5 class="no-record">No matching records found</h5>
            </td>
          </tr>
        </tbody>
      </table>
<app-custom-pagination></app-custom-pagination>
    </div>
  </div>
</div>
<!-- /product list -->
